I've been doing that for a while. Here's a simple class that does the trick:
<br />
<br />using System;
<br />using System.Security.Principal;
<br />using System.Web;
<br />
<br />namespace WildSoft.Security
<br />{
<br />    public class Impersonation : IDisposable
<br />    {
<br />        public Impersonation()
<br />        {
<br />            context = (HttpContext.Current.User.Identity as WindowsIdentity).Impersonate();
<br />        }
<br />
<br />        public void Dispose()
<br />        {
<br />            context.Undo();
<br />        }
<br />
<br />        WindowsImpersonationContext context;
<br />    }
<br />}
