It's actually quite easy to roll your own:
<br />
<br />using System;
<br />using System.ComponentModel;
<br />using System.Globalization;
<br />using System.Web;
<br />using System.Web.UI.WebControls;
<br />
<br />[DefaultProperty("PropertyName")]
<br />public class PagePropertyParameter : Parameter
<br />{
<br />    public PagePropertyParameter(string name, TypeCode typeCode, string propertyName)
<br />        : base(name, typeCode)
<br />    {
<br />        PropertyName = propertyName;
<br />    }
<br />
<br />    public PagePropertyParameter(string name, string propertyName)
<br />        : base(name)
<br />    {
<br />        PropertyName = propertyName;
<br />    }
<br />
<br />    protected PagePropertyParameter(PagePropertyParameter original)
<br />        : base(original)
<br />    {
<br />        PropertyName = original.PropertyName;
<br />    }
<br />
<br />    public PagePropertyParameter()
<br />    {
<br />    }
<br />
<br />    [DefaultValueAttribute("")]
<br />    [Category("Parameter")]
<br />    public string PropertyName
<br />    {
<br />        get
<br />        {
<br />            object value = ViewState["PropertyName"];
<br />            if (null == value) return string.Empty;
<br />            return (string)value;
<br />        }
<br />        set
<br />        {
<br />            if (string.IsNullOrEmpty(value))
<br />            {
<br />                ViewState.Remove("PropertyName");
<br />            }
<br />            else
<br />            {
<br />                ViewState["PropertyName"] = value;
<br />            }
<br />        }
<br />    }
<br />
<br />    protected override Parameter Clone()
<br />    {
<br />        return new PagePropertyParameter(this);
<br />    }
<br />
<br />    protected override object Evaluate(HttpContext context, Control control)
<br />    {
<br />        string prop = this.PropertyName;
<br />        if (string.IsNullOrEmpty(prop)) return null;
<br />
<br />        IHttpHandler handler = context.Handler;
<br />        if (null == handler) return null;
<br />
<br />        return DataBinder.Eval(handler, prop);
<br />    }
<br />}

