Try this...

class Decrapify {

	HttpRequest _request;
	HttpResponse _response;
	NameValueCollection _form;

	public Decrapify ( HttpRequest rq, HttpResponse rp ) {
		_request = rq;
		_response = rp;
		_form = _request.Form;
	}

	public Dictionary<string string="string"> Stripper () {

		Dictionary<string string="string"> temp = new Dictionary<string string="string"> ();

		foreach ( string k in _form.Keys ) {

			if ( k.Contains ( '$' ) ) {
				string strip = k.Substring(k.LastIndexOf('$') + 1);
				temp.Add ( strip, _form[k] );
			} else {
				temp.Add ( k, _form[k] );
			}
		}

		return temp;
	}

	public string Scraper ( string key ) {

		Dictionary<string string="string"> temp = Stripper ();

		if ( key != null &amp;&amp; key != "" ) {
			try {
				return temp[key];
			} catch ( Exception e ) {
			}
		}
		return null;
	}
}</string></string></string></string>

