An optimized code with GUID

public static class clsUtility
    {
        public static DataView RandomizeDataTable(DataTable dt)
        {
            if (dt.Columns["tmpSortColumn"] == null)
                dt.Columns.Add(new DataColumn("tmpSortColumn", typeof(string)));

            int rndSortColIdx = dt.Columns["tmpSortColumn"].Ordinal;

            for (int i = 0; i &lt; dt.Rows.Count; i++)
                dt.Rows[i][rndSortColIdx] = Guid.NewGuid().ToString();

            DataView dv = new DataView(dt);
            dv.Sort = "tmpSortColumn";
            return dv;
        }
    }
